nested.callback:
try {
	if (!property) return '';

	function esc(s) {
		if (s === null || s === undefined) return '';
		return String(s).replace(/&/g, '&amp;')
			.replace(/</g, '&lt;')
			.replace(/>/g, '&gt;')
			.replace(/"/g, '&quot;')
			.replace(/'/g, '&#039;');
	}

	function renderIcons(cost) {
		// return inline icon spans for each cost type (adjust SVGs if you prefer different icons)
		var out = '';
		if (!cost) return out;
		if (cost.stress != null) {
			out += '<div class="fcg-icon fcg-icon-stress" aria-label="' + esc(cost.stress) + ' Stress"></div>';
		}
		if (cost.fear != null) {
			out += '<div class="fcg-icon fcg-icon-fear" aria-label="' + esc(cost.fear) + ' Fear"></div>';
		}
		if (cost.hitPoints != null) {
			out += '<div class="fcg-icon fcg-icon-hp" aria-label="' + esc(cost.hitPoints) + ' HP"></div>';
		}
		return out;
	}

	function headerHtmlFor(f) {
		// Build the inner header markup (we will inject this into the host's outer .property-name)
		var nameHtml = '<span class="header-name">' + esc(f.name || '') + '</span>';
		var valueHtml = (f.value !== undefined && f.value !== null) ? '<span class="header-value">(' + esc(f.value) + ')</span>' : '';
		var typeHtml = f.type ? '<span class="header-type">' + esc(f.type) + '</span>' : '';

		var inner = '<div class="statblock-markdown"><p>';
		inner += nameHtml + '&nbsp;';
		if (valueHtml) inner += valueHtml + '&nbsp;';
		if (typeHtml) inner += '-&nbsp;<span class="header-type-wrap"><span class="header-type-inner">' + typeHtml + '</span></span>';
		inner += '</p></div>';

		// Return a small script that will find the nearest .property ancestor and
		// replace its .property-name content with our header. We JSON.stringify the
		// HTML to safely embed it in the script.
		var script = '<script>(function(){try{var s=document.currentScript; var html=' + JSON.stringify(inner) + '; var prop = s.closest(\'.property\'); if(prop){var pn = prop.querySelector(\'.property-name\'); if(pn) pn.innerHTML = html;} s.remove();}catch(e){} })();<\/script>';
		return script;
	}

	function renderFeatureBody(f) {
		var uses = (f.uses !== undefined && f.uses !== null) ? '<div class="uses">Uses: ' + esc(f.uses) + '</div>' : '';
		var costHtml = '';
		if (f.cost) {
			var parts = [];
			if (f.cost.stress != null) parts.push('stress');
			if (f.cost.fear != null) parts.push('fear');
			if (f.cost.hitPoints != null) parts.push('hitPoints');
			// icons: show the icons block using renderIcons
			costHtml = '<div class="cost-icons">' + renderIcons(f.cost) + '</div>';
		}
		var desc = f.description ? '<div class="desc">' + esc(f.description) + '</div>' : '';
		var flav = (f.flavourText || f.flavorText) ? '<div class="flavour"><em>' + esc(f.flavourText || f.flavorText) + '</em></div>' : '';
		return uses + costHtml + desc + flav;
	}

	// If plugin passes the whole array
	if (Array.isArray(property)) {
		// Group items under a single container so header is injected only once
		var items = property.map(function (f, i) {
			var rawValue = (f.value !== undefined && f.value !== null) ? ' (' + String(f.value) + ')' : '';
			var rawType = f.type ? ' - ' + String(f.type) : '';
			var rawHeader = (f.name || '') + rawValue + rawType;
			var slug = String(rawHeader).toLowerCase().replace(/[^a-z0-9]+/g, '-').replace(/^-+|-+$/g, '');

			var nameHtml = (i === 0) ? headerHtmlFor(f) : '';
			var bodyHtml = '<div class="statblock-markdown">' + renderFeatureBody(f) + '</div>';
			return '<div class="statblock-item-container statblock-trait-prop"><div class="property ' + esc(slug) + ' features trait">' + nameHtml + bodyHtml + '</div></div>';
		}).join('');

		return '<div class="statblock-item-container traits-container">' + items + '</div>';
	}

	// If plugin calls per-item, the renderer may print its own property-name; we still return
	// a property-name element in the body (so the header styling is consistent). The CSS snippet
	// below will hide the renderer's name when this inner property-name exists.
	var nameHeader = headerHtmlFor(property);
	var bodyContent = '<div class="statblock-markdown">' + renderFeatureBody(property) + '</div>';
	return nameHeader + bodyContent;
} catch (e) {
	return '';
}

diceParsing.parser:
let expr = matches[1].trim();
if (!expr) return null;

// Remove spaces and normalize 'D' to lowercase 'd'
expr = expr.replace(/\s*([dD+-])\s*/g, m => m.trim().toLowerCase());

// If the expression is a standalone number (e.g., '+3', '-2', '5'), convert to 1d20+N
if (/^[+-]?\d+$/.test(expr)) {
  let n = Number(expr);
  if (isNaN(n)) return null;
  expr = `1d20${n >= 0 ? '+' : ''}${n}`;
}

return { text: expr, original };
